western.edu/information-technology\", \"mccormick-dev.tech.northwestern.edu/robotics\", \"mccormick-dev.tech.northwestern.edu/project-management\",\"mccormick-dev.tech.northwestern.edu/executive-management-design-construction\",\"mccormick-dev.tech.northwestern.edu/theoretical-applied-mechanics\",\"mccormick-dev.tech.northwestern.edu/predictive-science-engineering-design\", \"mccormick-dev.tech.northwestern.edu/artificial-intelligence\"];\n  var mccProgramSites = [\"mccormick.northwestern.edu/analytics\",\"mccormick.northwestern.edu/biotechnology\", \"mccormick.northwestern.edu/engineering-management\", \"mccormick.northwestern.edu/information-technology\", \"mccormick.northwestern.edu/robotics\", \"mccormick.northwestern.edu/project-management\", \"mccormick.northwestern.edu/executive-management-design-construction\",\"mccormick.northwestern.edu/theoretical-applied-mechanics\",\"mccormick.northwestern.edu/predictive-science-engineering-design\", \"mccormick.northwestern.edu/artificial-intelligence\"];\n  var mccdevDivisionSites = [\"mccormick-dev.tech.northwestern.edu/eecs/computer-science\",\"mccormick-dev.tech.northwestern.edu/eecs/electrical-engineering\",\"mccormick-dev.tech.northwestern.edu/eecs/computer-engineering\"];\n  var mccDivisionSites = [\"mccormick.northwestern.edu/eecs/computer-science\",\"mccormick.northwestern.edu/eecs/electrical-engineering\",\"mccormick.northwestern.edu/eecs/computer-engineering\"];\n  var allDivisionSites = mccdevDivisionSites.concat(mccDivisionSites);\n  var allmccSites = mccdevSites.concat(mccSites);\n\n  for (var s in allmccSites){\n  /* for each McCormick department site, live and dev, in the array, check if it is the root of the referrer link so included functions only run once for the case it matches. \n  When that is the case, check if the referring link is from a division site. If it is, run the division back link function. If it's not run the department link function.  */\n   if (document.referrer.indexOf(allmccSites[s]) != -1){\n      \n      if (isReferrerFromDivision()) {\n        var divSite = isReferrerFromDivision();\n        console.log(divSite);\n        buildDivisionBackLink(divSite); \n      \n      } else {\n        buildDeptBackLink(allmccSites[s]);\n      \n        }\n   }\n  }\n\n  for (var s in mccdevProgramSites){\n    buildProgramBackLink(mccdevProgramSites[s]);\n    /* The build program link function now runs for every site url root in the array, even though the if statements only execute for the one url that matches the referrer. May need to insert qualifier like in loop above. */\n  }\n\n  for (var s in mccProgramSites){\n    buildProgramBackLink(mccProgramSites[s]);\n  }\n\nfunction buildDeptBackLink(mccWebsite){\n \n        if (isReferrerFromPeople(mccWebsite) && isThisNewsArticle()){\n          $('#news-article-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department</a></div>\");\n          $('#news-article-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department</a></div>\");\n\n      } else if (isReferrerFromPeople(mccWebsite)){\n        $('#main-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department Faculty</a></div>\");\n        $('#main-content div:last').before(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department Faculty</a></div>\");\n      \n      } else if (isReferrerFromDeptOrPrgrm(mccWebsite)  && isThisFacProfile()){\n        $('#main-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department</a></div>\");\n        $('#main-content div:last').before(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department</a></div>\");\n\n      } else if (isReferrerFromNews(mccWebsite) && isThisNewsArticle()){\n        $('#news-article-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department News</a></div>\");\n        $('#news-article-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department News</a></div>\");\n      \n      } else if (isReferrerFromNews(mccWebsite) && !isReferrerFromEECS() && !isThisEECS()){\n        $('#main-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department News</a></div>\");\n        $('#main-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department News</a></div>\");\n      \n      } else if (isReferrerFromDeptOrPrgrm(mccWebsite) && isThisNewsArticle()){\n          $('#news-article-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department</a></div>\");\n          $('#news-article-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department</a></div>\");\n\n      } else if (isReferrerFromDeptOrPrgrm(mccWebsite) && !isReferrerFromEECS() && !isThisEECS()){\n          $('#main-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department</a></div>\");\n          $('#main-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Department</a></div>\");\n      }  \n}\n\nfunction buildProgramBackLink(mccWebsite){\n\n        if (isReferrerFromFaculty(mccWebsite) && isThisNewsArticle()){\n          $('#news-article-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program</a></div>\");\n          $('#news-article-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program</a></div>\");\n\n      } else if (isReferrerFromFaculty(mccWebsite)){\n        $('#main-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program Faculty</a></div>\");\n        $('#main-content div:last').before(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program Faculty</a></div>\");\n      \n      } else if (isReferrerFromDeptOrPrgrm(mccWebsite)  && isThisFacProfile()){\n        $('#main-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program</a></div>\");\n        $('#main-content div:last').before(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program</a></div>\");\n        \n      } else if (isReferrerFromNews(mccWebsite) && isThisNewsArticle()){\n        $('#news-article-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program News</a></div>\");\n        $('#news-article-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program News</a></div>\");\n      \n      } else if (isReferrerFromNews(mccWebsite) && !isThisEECSNews()){\n        $('#main-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program News</a></div>\");\n        $('#main-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program News</a></div>\");\n      \n      } else if (isReferrerFromDeptOrPrgrm(mccWebsite) && isThisNewsArticle()){\n          $('#news-article-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program</a></div>\");\n          $('#news-article-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program</a></div>\");\n\n      } else if (isReferrerFromDeptOrPrgrm(mccWebsite) && !isThisEECSNews()){\n\n          $('#main-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program</a></div>\");\n          $('#main-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Program</a></div>\");\n      }  \n}\n\nfunction buildDivisionBackLink(divSite){\n\n        if (isReferrerFromNews(divSite) && isThisNewsArticle()){\n          $('#news-article-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division News</a></div>\");\n          $('#news-article-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division News</a></div>\");\n\n      } else if (isThisNewsArticle()){\n          $('#news-article-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division</a></div>\");\n          $('#news-article-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division</a></div>\");\n\n      } else if (isReferrerFromNews(divSite) && isThisEECSNews()){\n          $('#news-article-content').prepend(\"<div id=\\\"back-to-dept\\\" style=\\\"margin-top:15px;\\\" ><a href=\\\"\" + document.referrer + \"\\\">Back to Division News</a></div>\");\n          $('#news-article-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division News</a></div>\");\n\n      } else if (isThisEECSNews()){\n          $('#news-article-content').prepend(\"<div id=\\\"back-to-dept\\\" style=\\\"margin-top:15px;\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division</a></div>\");\n          $('#news-article-content').append(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division</a></div>\");\n\n      } else if (isReferrerFromPeople(divSite)){\n          $('#main-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division Faculty</a></div>\");\n          $('#main-content div:last').before(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division Faculty</a></div>\");\n      \n      } else if (isThisFacProfile()){\n          $('#main-content').prepend(\"<div id=\\\"back-to-dept\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division</a></div>\");\n          $('#main-content div:last').before(\"<div id=\\\"back-to-dept\\\" class=\\\"second-back\\\"><a href=\\\"\" + document.referrer + \"\\\">Back to Division</a></div>\");\n      }\n}\n  \n   function isThisFacProfile(){\n      return window.location.href.indexOf(\"/research-faculty/directory/\") != -1;\n   }     \n\n   function isThisNewsArticle(){\n      if (window.location.href.indexOf(\"/news/articles\") != -1 && window.location.href.indexOf(\"eecs/news/articles\") == -1 ){\n        return true;\n      };\n   }\n\n   function isReferrerFromPeople(mccWebsite){\n      return document.referrer.indexOf(mccWebsite + \"/people\") != -1;  \n   }\n\n   function isReferrerFromFaculty(mccWebsite){\n      return document.referrer.indexOf(mccWebsite + \"/faculty\") != -1;\n   }\n\n   function isReferrerFromNews(mccWebsite){\n      return document.referrer.indexOf(mccWebsite + \"/news\") != -1;  \n   }\n\n   function isReferrerFromDeptOrPrgrm(mccWebsite){\n      return document.referrer.indexOf(mccWebsite) != -1;\n   }\n\n   function isReferrerFromEECS(){\n      if (document.referrer.indexOf(\"mccormick.northwestern.edu/eecs/\") != -1 || document.referrer.indexOf(\"mccormick-dev.tech.northwestern.edu/eecs/\") != -1){\n        return true;\n      }\n   }\n\n   function isThisEECS(){\n      if (window.location.href.indexOf(\"mccormick.northwestern.edu/eecs/\") != -1 || window.location.href.indexOf(\"mccormick-dev.tech.northwestern.edu/eecs/\") != -1){\n        return true;\n      } \n   }\n\n   function isThisEECSNews(){\n      if (window.location.href.indexOf(\"mccormick.northwestern.edu/eecs/news\") != -1 || window.location.href.indexOf(\"mccormick-dev.tech.northwestern.edu/eecs/news\") != -1){\n        return true;\n      } else {\n        return false;\n      }\n   }\n\n\n   function isReferrerFromDivision(){\n      var urlBooleans = [];\n      for (var y in allDivisionSites){\n      \n        if (document.referrer.indexOf(allDivisionSites[y]) != -1){\n          urlBooleans.push(allDivisionSites[y]);\n        }\n      }\n    \n      if (urlBooleans.length > 0){ \n        return urlBooleans[0];\n      } else {\n        return false;\n        console.log(\"not from division\");\n      }\n   }\n\n</script>\n\r\n\r\n<script type=\"text/javascript\">\r\n\r\n  var _gaq = _gaq || [];\r\n _gaq.push(['_setAccount', 'UA-3977631-26']);\r\n _gaq.push(['_trackPageview']);\r\n\r\n  _gaq.push(['b._setAccount', 'UA-3977631-1']);\r\n _gaq.push(['b._trackPageview']);\r\n\r\n  _gaq.push(['_setDomainName', '.northwestern.edu']);\r\n\r\n  (function() {\r\n    var ga = document.createElement('script'); ga.type = 'text/javascript'; ga.async = true;\r\n    ga.src = ('https:' == document.location.protocol ? 'https://ssl' : 'http://www') + '.google-analytics.com/ga.js';\r\n    var s = document.getElementsByTagName('script')[0]; s.parentNode.insertBefore(ga, s);\r\n  })();\r\n\r\n</script>\r\n\r\n\n<script src=\"/js/mti-tracking.js\" type=\"text/javascript\"></script>\n \r\n\r\n\r\n</body>\r\n</html>","name":"Gokhan Memik","address":"2145 Sheridan Road Tech Room L475 Evanston, IL 60208-3109","phone_number":"847-467-1168","email":"g-memik@northwestern.edu","websites":{"Gokhan Memik's Hompage":"http://www.ece.northwestern.edu/~memik/","Current Projects":"http://www.eecs.northwestern.edu/~memik/proj.html","Center for Ultra-scale Computing and Information Security (CUCIS)":"http://cucis.ece.northwestern.edu/","Center for Engineering and Health":"http://www.feinberg.northwestern.edu/sites/ipham/centers/engineering-health.html"},"personal_website":"http://www.ece.northwestern.edu/~memik/","departments":[],"affiliations":[],"education":["Ph.D. Electrical Engineering, UCLA, Los Angeles, CA","M.S. Electrical and Computer Engineering, Northwestern University, Evanston, IL","B.S. Computer Engineering, Bogazici Univeristy, Istanbul, Turkey"],"interests":"Prof. Memik works in the area of computer architecture. His work spans three major areas: (1) understanding/developing new technologies and developing architectures that are more suitable for these technologies, (2) modeling large-scale systems and optimizing them, and (3) developing techniques that can automatically detect user needs/requirements and use this information to improve performance of mobile system and reduce their power consumption. "}
